INSERT INTO `drupal_comments` VALUES (4062,0,1037,206,'Koordynator ds. IT, kandydat: Marcin Mikołajczak','Mocne strony:\r\n - odpowiedzialność\r\n - dobre zorganizawanie\r\n - dbałość o szczegóły\r\n - wiedza przedmiotowa\r\n\r\nSłabe strony:\r\n - ograniczona ilość czasu\r\n - umiejętności interpersonalne wciąż wymagające pewnego doszlifowania\r\n\r\nSzanse:\r\n - zbudowanie atrakcyjnego Zespołu ds. IT\r\n - stworzenie nowoczesnej i sprawnej platformy komunikacyjnej dla SKND\r\n\r\nZagrożenia:\r\n - brak osób w nowej rekrutacji zainteresowanych tematyką IT','83.20.139.248',1224534546,0,4,'03/','',NULL,NULL);
INSERT INTO `drupal_comments` VALUES (4063,0,1037,206,'Koordynator ds. IT, kandydat: Marcin Mikołajczak','3 priorytetowe kwestie to:\r\n - zbudowanie zespołu zainteresowanego zarządzaniem w IT\r\n - zapewnienie stabilności platformy Google Apps / Moodle\r\n - zbudowanie nowej strony SKND w oparciu o Drupala','83.20.139.248',1224534671,0,4,'04/','',NULL,NULL);
