INSERT INTO `drupal_comments` VALUES (4066,0,1037,206,'Koordynator ds. IT, kandydat: Marcin Mikołajczak','Myślę, że moją motywację można by podzielić na takie dwa główne nurty.\r\n\r\nPo pierwsze Stowarzyszenie daje mi możliwość poznania wielu interesujących i energicznych ludzi. To jest dla mnie wyjątkowo wartościowe, móc obcować z ludźmi, którzy gdzieś u podstaw swojej aktywności mają sprzeciw wobec marazmu i bylejakości. \r\n\r\nA druga sprawa to możliwość wprowadzenia mojej wiedzy i umiejętności w życie (mam tutaj na myśli IT) z pożytkiem dla wszystkich, czyli zastosowanie technoloii do rzeczywistego wspomagania ludzkiej aktywności. To jest chyba podstawowa funkcja technologii, o której niestety się często w tej branży zapomina.','83.20.139.248',1224695443,0,4,'07/','',NULL,NULL);
