INSERT INTO `drupal_comments` VALUES (4083,0,1039,183,'Członek Zwyczajny SKND, kandydat: Maciej Popkowski','Jeśli chodzi o moją działalność w tym roku to chciałbym koordynować projekt. Oczywiście nie zapominając o zespołach stałych.','83.20.139.248',1224671194,0,4,'04/','',NULL,NULL);
