INSERT INTO `drupal_comments` VALUES (4085,0,1039,183,'Członek Zwyczajny SKND, kandydat: Maciej Popkowski','Nie jestem jeszcze do końca zdecydowany, ale myślę żeby działac w innych zespołach niż w zeszłym roku. Chcę \"dązyć do doskonałości\" i ciągle się rozwijać- oczywiście w dziedzinach, które mnie interesują.','83.20.139.248',1224698765,0,4,'06/','',NULL,NULL);
