INSERT INTO `drupal_comments` VALUES (4107,0,1047,161,'Gratulacje!','Tym razem GRATULACJE dla Agnieszki Matysiak i Leszka Bździela - naszych dwóch alumnów, którzy w ostatnią sobotę zawarli związek małżeński!\r\n\r\nAgnieszko i Leszku - życzymy wszystkiego najlepszego na nowej drodze życia!\r\n\r\nmam nadzieję, że za jakiś czas ukażą się na forum zdjęcia z zaślubin...\r\n\r\nŻyczę też udanego miesiąca miodowego..:)','83.20.139.248',1231781028,0,4,'03/','',NULL,NULL);
