INSERT INTO `drupal_comments` VALUES (2238,0,598,147,'Pozyskanie Partnera Telekomunikacyjnego','Pragniemy nawiązać trwałą współpracę z firmą, który zapewni określoną ilość telefonów komórkowych, \r\nniezbędnych w pracach zespołów stałych oraz projektowych, a także do dalszego rozwoju Stowarzyszenia.\r\n\r\nKontaktowanie:\r\n\r\nLeszek - PLAY, ERA\r\nPatrycja - ORANGE\r\nAnia - PLUS\r\n\r\nOferty dla poszczególnych operatorów leżą u nich na biurkach. Czekamy ...','83.20.139.248',1176133128,0,4,'01/','',NULL,NULL);
