INSERT INTO `drupal_comments` VALUES (2395,0,623,164,'materialy','witam, \r\n\r\ndostalem materialy. \r\n\r\nI tak:\r\n\r\n1) certykikat mam totalnie pognieciony\r\n2) pudelko od plytki (na szczescie nie plytka) totalnie polamane\r\n\r\npamietajmy na przyszlosc - nie robic nigdy takiej fuszery:)\r\n\r\npozdrawiam\r\ntomas','83.20.139.248',1182939777,0,4,'0z/','',NULL,NULL);
INSERT INTO `drupal_comments` VALUES (2391,0,623,164,'NOCLEG','witam, \r\n\r\noto strona noclegu: http://rapsodia.com.pl/\r\n\r\nadres: ul. Fort Wola 22, Warszawa\r\n\r\nObawiam sie jednak, ze do noclegowni trzeba na poczatku pojawic sie ze mna (nocleg zalatwiony jest tanszy - dostalismy rabat od dobrego znajomego) i jeszcze nie znam procedury otrzymania kluczy itp. (moze ona nie byc standardowa). \r\n\r\nDlatego sugeruje, bysmy do noclegowni zladowali po Wykladach (ew. w przerwie) w srode, a z bagazami problemu nie bedzie, bo na pewno bedzie je mozna zostawic w szatni na uczelniach.\r\n\r\nZalaczam tez mapke. Ta glowna ulica, od ktorej odchodzi Fort Wola, to ul. Wolska (wyjezdzajaca naPoznan)','83.20.139.248',1177418212,0,4,'0v/','',NULL,NULL);
