INSERT INTO `drupal_comments` VALUES (4238,4193,1093,155,'Re: ostry dyżur','Polaczyles tu dwie rzeczy: niestandardowa odpowiedz + poczucie humoru - to sa dwie rzeczy, ktore robia bardzo dobre wrazenie na ludziach prowadzacych rozmowe. Jezeli kandydat zdobywa sie na cos niesztampowego i jeszcze do tego \"z jajem\" to znaczy, ze ma charakter i nie pozwala sie zrzerac stresowi. TYlko jak ze wszystkim: kreatywnosc na sile i zamienienie rozmowy w kabaret chyba jeszcze nikomu nie zaimponowaly:)','62.21.78.119',1232392135,0,4,'0f.00/','wojciech.walniczek','','');
