INSERT INTO `drupal_comments` VALUES (2342,0,621,163,'Projekty SKND semestrze letnim 2006/2007','Mamy już ludzi, mamy projekty, czas zabierać się do pracy!\r\n\r\nOto lista projektów i osób, które zdecydowały się przyjąć wyzwanie:\r\n\r\n[b][color=green]Anna Pomin i Rafał Madoński[/color][/b]\r\nDzień z Audytem \r\n\r\n[color=green][b]Marek Nadra i Maciej Wesołowski[/b][/color]\r\nDzień z IT \r\n\r\n[color=green][b]Magdalena Marszałek[/b][/color]\r\nPromocja Konsultingu \r\n\r\n[color=green][b]Łukasz Trojanowski[/b][/color]\r\nErnst & Young Day \r\n\r\n[b][color=green]Aleksandra Dettlaff[/color][/b]\r\nSurvive\'2007\r\n\r\n[b][color=green]Lechosław Bździel[/color][/b]\r\nValues Driven Organizations\r\n\r\n[b][color=green]Natalia Słocińska[/color][/b]\r\nKonkurs Wiedzy Ekonomicznej\r\n\r\n[b][color=green]Bartosz Kobus[/color][/b]\r\nKOMPAS\r\n\r\n\r\nW imieniu Zarządu pragnę życzyć Wam powodzenia w projektowych zmaganiach, zapewnić o stałym wsparciu z naszej strony oraz zachęcić do podejmowania wysiłków w walce o wspólną sprawę! \r\n\r\nDo pracy! Czas start!\r\n\r\nMaciej Hojszyk','83.20.139.248',1172706500,0,4,'01/','',NULL,NULL);
