INSERT INTO `drupal_comments` VALUES (2258,0,609,164,'VISTA wio!','witam, \r\n\r\ntak sobie obserwuje, co sie dzieje na rynku i widze, ze Windows vista wszedzie sie reklamuje. Przedstawiciele Microsoft biora udzial w bardzo wielu konferencjach, wydarzeniach IT i wyjasniaja, jakie nowosci sa w Vista. \r\n\r\nJestem niemalze pewny, ze gdyby z nimi porozmawiac w odpowiedni sposob, to przyslaliby kogos na nasz Dzien z IT (i jeszcze zrobiliby to za darmo). A moze udaloby sie wynegocjowac jakies nagrody w postaci programow Vista? \r\n\r\ntaka ot mysl:)\r\n\r\npozdrawiam','83.20.139.248',1171012043,0,4,'02/','',NULL,NULL);
