INSERT INTO `drupal_comments` VALUES (2296,0,616,158,'Zapowiedzi spotkań','Witam,\r\n\r\nznowuż trzeba uznać wczesną sobotnią porę za piątkowy wieczór;) przepraszam za opóźnienie:\r\n\r\nZespół spotyka się 17 lutego o 13.30 w biurze SKND\r\n\r\nAgenda\r\n\r\nUpdate\r\nCele na ten semestr\r\nOrganizacja przyjazdu banków inwestycyjnych na jesień\r\nArtykuły, Dalsze prace nad stroną\r\nKosztorys\r\nInne:)\r\n\r\nDo zobaczenia \r\n\r\nMarysia','83.20.139.248',1171672176,0,4,'01/','',NULL,NULL);
INSERT INTO `drupal_comments` VALUES (2297,0,616,158,'kontaktowanie','Witam,\r\n\r\nMoi drodzy zgodnie z tym co jest w raporcie :D spotykamy się w czwartek o 18.30 w Biurze SKND, aby omówić sprawę kontaktowania.\r\n\r\n1. Grafik kontaktowania\r\n2. Elementy potrzebne do kontaktowania\r\n3. Niestandardowe formy\r\n\r\nPozdrawiam i zapraszam również pomoc z SKND...\r\n\r\nMaria','83.20.139.248',1171821306,0,4,'02/','',NULL,NULL);
