INSERT INTO `drupal_comments` VALUES (2714,0,723,137,'Projekty SKND w roku akademickim 2007/2008r.','Jeśli mnie kalendarz nie myli to 22-23 marca (kiedy planowany jest Dzień z IT) to weekend, a w dodatku jest to weekend świąteczny.\r\nPrzerwa świąteczna na Akademii Ekonomicznej trwa od 21 do 26 marca 2008.\r\nAle to nie powinno przeszkadzać w tym, żeby zrobić Dzień z Audytem 18-19 marca, a Dzień ze Strategią 1 kwietnia. Tylko z tą polibudą trzeba by coś zmienić...','83.20.139.248',1189606583,0,4,'08/','',NULL,NULL);
INSERT INTO `drupal_comments` VALUES (2715,0,723,159,'Projekty SKND w roku akademickim 2007/2008r.','Witam,\r\n\r\nKoordynatorzy poszczególnych projektów powinni być wybrani do końca października br.. Do końca września ogłoszę nabór na koordynatorów projektów i deadline zgłaszania swoich kandydatur. Zarządowi zależy na tym, aby każdy projekt miał przypisanego koordynatora już w tym roku. Wcześnie powołany koordynator, to więcej czasu na realizację wizji wydarzenia (dokładniej i lepiej;) \r\n\r\nPozdrawiam \r\nBartek','83.20.139.248',1189702416,0,4,'09/','',NULL,NULL);
