INSERT INTO `drupal_comments` VALUES (2996,0,793,138,'Konkursy / Gry','Moi Drodzy, chciałbym poinformować, iż w ramach jednej z Naszych, Wspólnych wartości „ Uczymy się od siebie nawzajem” Zespól ds. Grantów wszedł w posiadanie Strategiczno – Ekonomicznej Gry [b]Cash Flow 101 i Cash Flow 202[/b], których autorem jest Robert Kiyosaki – twórca światowego bestsellera  „Bogaty Ojciec Biedny Ojciec” – Gra jest na prawde wartosciowa, można z niej nauczyc się jak zarządzać wlasnymi finansami, dowiemy się co to jest pasywny dochód. Polecam przede wszystkim studentom kierunków nieekonomicznych.\r\nPo co macie wydawać 500 zł skoro możecie to uzyskać za darmo.\r\nZainteresowanych proszę o napisanie maila do mnie w celu wypożyczenia tej gry.\r\nJest to wersja nie planszowa - a elektroniczna.','83.20.139.248',1194028999,0,4,'01/','',NULL,NULL);
