INSERT INTO `drupal_comments` VALUES (3251,0,843,181,'Spotkanie Treningowo-Integracyjne 6 - 2008','Ja oczywiście wchodzę. W końcu ktoś musi Ciebie pilnować, mężczyzno rasy negroidalnej :wink:.\r\n\r\nNo i zachęcam oczywiście wszystkich do pomocy. STI to akurat takie przedsięwzięcie, gdzie każdy może śmiało dorzucić jakiś odjechany pomysł i liczyć, że choć w części zostanie wykorzystany (pomysł oczywiście, nie osoba pomysł dorzucająca).','83.20.139.248',1197203053,0,4,'0f/','',NULL,NULL);
INSERT INTO `drupal_comments` VALUES (3252,0,843,145,'Spotkanie Treningowo-Integracyjne 6 - 2008','[quote=\"Mateusz_Matloka\"]Ja oczywiście wchodzę. W końcu ktoś musi Ciebie pilnować, Czarnuchu :lol:.\r\n[/quote]\r\n\r\nDobra, dobra - i tak wiemy, że chodzi Ci o te wszystkie dziewczyny, które licznie zapisują się do teamu :wink: \r\npozdrawiam','83.20.139.248',1197267692,0,4,'0g/','',NULL,NULL);
