INSERT INTO `drupal_comments` VALUES (3305,0,851,145,'Dzień z IT 2008','Znowu trochę się ruszyło...\r\n\r\n22. kwietnia będziemy gościli następujące firmy na PP: Microsoft, Sun Microsystems, ICG Wdrożenia SAP, PB Polsoft, Accenture, Infovide-Matrix, Allegro\r\n\r\nPatronem medialnym wydarzenia jest portal: www.studentnews.pl \r\n\r\nPatronem honorowym jest Politechnika Poznańska\r\n\r\nNa wykładzie otwartym pojawi się przedstawiciel Start-Up IT oraz prawdopodobnie ktoś z Allegro. Temat to generalnie \"Startupy IT\".\r\n\r\nDo wygrania będą telefony komórkowe, pakiety MS Office i inne programy MS :!: \r\n\r\nWkrótce (pewnie w ciągu 20h) na stronie www.dzienzit.pl pojawią się szerokie aktualizacje - check it out!\r\n\r\nps. [b]Poszukujemy fotografa Dnia z IT, najlepiej od 9-16:30;)[/b]','83.20.139.248',1208045721,0,4,'08/','',NULL,NULL);
