INSERT INTO `drupal_comments` VALUES (3311,0,852,181,'Promocja SKND online, yeah','A możesz powiedzieć czym jeszcze zajmowaliście się na Jakości w związku właśnie z promocją SKND w sieci?\r\n\r\nDo tego jeszcze w grantach mamy przecież projekt z wynajdywaniem miejsc do promocji SKND w sieci w celu zaistnienia dla potencjalnych grantodawców...\r\n\r\n\r\nMoże by po prostu powołać kilkuosobowy zespół składający się z ludzi z różnych działów, który opracowałby strategię obecności SKND w sieci oraz skrupulatnie ją realizował w przeciągu całego roku? Co myślicie?','83.20.139.248',1196378923,0,4,'02/','',NULL,NULL);
