INSERT INTO `drupal_comments` VALUES (3331,0,856,164,'no ladne rzeczy','hola, hola, \r\n\r\nładnie to tak na forum obgadywać Prezesa?;)\r\n\r\nosobiście bardzo się cieszę z tej nagrody. Miło mi, że Wy również. \r\n\r\nPolecam WSZYSTKIM Wam, byście przed/po napisaniu pracy rozejrzeli się, czy z tematyki pracy mgr jakaś instytucja nie organizuje konkursu - pracę mgr i tak musicie napisać, a dodatkowo możecie wziąć udział w konkursie, po prostu ją wysyłając i kto wie - a nuż się uda. Naprawdę warto. \r\n\r\npozdrawiam, pisząc kolejną:)\r\ntomasz\r\n\r\nP.S. Takie inicjatywy, jak ten konkurs, utwierdzają mnie w przekonaniu, że:\r\n1) warto być studentem:)\r\n2) warto szukać\r\n3) warto zmuszać się do dobrej roboty i robić ją z radością\r\n4) warto być w kraju i nie trzeba wyjeżdżać, by było dobrze.','83.20.139.248',1196556427,0,4,'02/','',NULL,NULL);
