INSERT INTO `drupal_comments` VALUES (3333,0,857,140,'Szkolenia','[center][img]http://www.mikuszewo.pl/images/projekty/m_2cfd034f7336e60c6a51e6f656235419.jpg[/img][/center]\r\nOd listopada trójka Dziewczyn z Zespołu ds. PR bierze udział w cyklu szkoleń [color=green][b]\"Dama PIK\"[/b][/color], zorganizowanym przez Stowarzyszenie Inicjatyw Niezależnych \"Mikuszewo\", a dofinansowanym z Funduszu Inicjatyw Obywatelskich. \r\n\r\nCykl obejmuje trzy dwudniowe spotkania, podczas których przeprowadzane są zajęcia warsztatowe z zakresu:\r\n\r\n[u]17-18 listopada[/u]\r\n• [b]Rozwój lokalny[/b]\r\n - Identyfikacja zasobów, problemów i potrzeb lokalnych\r\n - Mobilizowanie i organizowanie społeczności lokalnej do działania, budowanie partnerstwa lokalnego\r\n\r\n[u]1-2 grudnia[/u]\r\n• [b]PR i kontakty z mediami[/b]\r\n - Public Relations organizacji pozarządowych\r\n - Konakty z mediami - przygotowanie wypowiedzi dla mediów, mistrzowska notatka prasowa\r\n\r\n[u]15-16 grudnia[/u]\r\n• [b]Autoprezentacja - komunikacja i wystąpienia publiczne[/b]\r\n\r\n[center][img]http://www.mikuszewo.pl/galerie/damapik/m_1200925717.jpg[/img] [img]http://www.mikuszewo.pl/galerie/damapik/m_1200925688.jpg[/img] [img]http://www.mikuszewo.pl/galerie/damapik/m_1200924883.jpg[/img][/center]','83.20.139.248',1196688323,0,4,'01/','',NULL,NULL);
