INSERT INTO `drupal_comments` VALUES (3557,0,912,134,'sknd.org.pl','A czy w tym kwadracie, gdzie dajemy nadchodzące wydarzenia nie będziemy pisać też, że nadchodzi jakieś wewnętrzne wydarzenie, na które nie zapraszamy wszystkich? :) \r\n\r\n\"WKRÓTCE\" wydaje się być całkiem OK, \"WYDARZENIA\" jeśli nie wymyślimy czegoś innego może zawierać przeszłe wydarzenia :) Nowych pomysłów brak - może to dlatego, że brałam udział w burzy mózgów dotyczącej obecnych nazw na www :D','83.20.139.248',1213044952,0,4,'0q/','',NULL,NULL);
