INSERT INTO `drupal_comments` VALUES (3678,0,933,188,'Project Management Days 2008','Hej,\r\n\r\nporuszyłem moje znajome osoby w Warszawie, i oto, co udało im się znaleźć:\r\n\r\nhttp://www.int.pjwstk.edu.pl/doc/lista.htm\r\n\r\nJest to lista akademików, niektóre z nich wynajmują pokoje gościnne (część tylko weekendy) w cenie ok. 30zł/dobę. Choć inne źródła mówiły, że może być z tym ciężko i nalepiej szukać nocelgu po dobrych i uczynnych znajomych... :/','83.20.139.248',1208488383,0,4,'06/','',NULL,NULL);
