INSERT INTO `drupal_comments` VALUES (4337,0,1130,152,'Re: Akademia Umiejętności w ramach Dni Kariery','Ja gorąco zachęcam do wzięcia udziału w szkoleniu PRINCE2. PRINCE2 jest to metodyka zarządzania projektami, wiedza z tego zakresu na pewno przyda się obecnym oraz przyszłym koordynatorom projektów :) \r\n\r\nWięcej o PRINCE2 http://pl.wikipedia.org/wiki/PRINCE2\r\n\r\nNa to szkolenie zostało jedynie 17 wolnych miejsc (które z pewnością szybko się rozejdą), więc proszę się spieszyć :)\r\n\r\n\r\n\r\n\r\n','62.148.83.174',1236218137,0,4,'01/','magdalena.przezdziak','','');
INSERT INTO `drupal_comments` VALUES (4338,0,1101,228,'Re: Podręcznik trenera','No i jest!\r\n\r\n[b]Kapitan - Podręcznik Trenera Cognitis![/b]\r\n\r\na co w nim?\r\n\r\n[b]Rozdział 1. Przed wypłynięciem w rejs [/b]................................................................................... 3\r\nWstęp .................................................................................................................................... 4\r\nCzym jest szkolenie............................................................................................................... 5\r\nPrzygotowanie prowadzącego.............................................................................................. 7\r\nPrzygotowanie sali .............................................................................................................. 11\r\nKonspekt .............................................................................................................................. 13\r\n\r\n[b]Rozdział 2. Na morzu[/b].............................................................................................................. 17\r\nJak rozpocząć ....................................................................................................................... 18\r\nTwoje ciało mówi ................................................................................................................ 20\r\nAtmosfera i motywacja ....................................................................................................... 24\r\nIcebreakery .......................................................................................................................... 27\r\nFormy szkoleniowe ............................................................................................................. 29\r\nPraca z grupą ....................................................................................................................... 34\r\nPraca czasem........................................................................................................................ 38\r\nTrudne sytuacje ................................................................................................................... 41\r\nZakończenie, podsumowanie ............................................................................................. 43\r\n\r\n[b]Rozdział 3. Dopłynąwszy do celu[/b]........................................................................................... 45\r\nMateriały szkoleniowe........................................................................................................ 46\r\nEwaluacja ............................................................................................................................. 49\r\nKreatywny Kapitan ............................................................................................................. 51\r\nRaport poszkoleniowy ........................................................................................................ 54\r\nPodsumowanie .................................................................................................................... 55\r\n\r\n\r\nJeżeli interesujesz się prowadzeniem szkoleń w Stowarzyszeniu i nie tylko, skontaktuj się z wiceprezesem, a z pewnością prześle Ci nowe materiały.\r\n\r\nDO zobaczenia na żeglarskim szlaku!','77.253.25.150',1236260324,0,4,'04/','julian.dabrowski','','');
