INSERT INTO `drupal_comments` VALUES (4454,0,908,152,'Re: ZARZĄD - bliżej:)','.\r\n.\r\n.\r\nZ archiwum G10.\r\n\r\nMiejsce: Biuro SKND\r\n\r\nCzas: 5 marca 2009 r.\r\n.\r\n.\r\n.\r\n\r\nCztery zidentyfikowane kobiety i jeden mężczyzna\r\n\r\npo podsumowaniu działań z ostatniego okresu,\r\n\r\nustalili zakres w jakim mogą działać Członkowie Komisji Rewizyjnej, \r\n\r\npodjęli decyzje o przekazaniu swoich formularzy realizacji celów KR\r\n\r\noraz o nie powoływaniu oficjalnego mentora projektu Marketplace\r\n\r\n(jako, że funkcja mentora wymaga dookreślenia i doprecyzowania),\r\n\r\nzapoznali się z tematami zadań Zespołów Budyniowych,\r\n\r\nustalili kilka technicznych kwestii dotyczących STI8\r\n\r\ni odbywającym się na nim WZ,\r\n\r\nzrewidowali swoje plany rozmów z Członkami SKND,\r\n\r\nzaplanowali wyjazd Koordynatorów,\r\n\r\nomówili zasady współpracy i usprawnienie komunikacji z Zespołem BCC,\r\n\r\npodsumowali dotychczasowe działania pod kryptonimem W&W*,\r\n\r\nporozdzielali między siebie zadania do wykonania\r\n\r\ni ulotnili się z tajemniczego G10.\r\n.\r\n.\r\n.\r\n\r\nDo następnego odcinka :)\r\n.\r\n.\r\n.\r\n\r\n*Warszawa&Wrocław\r\n.\r\n.\r\n.','62.148.83.174',1239105181,0,4,'0o/','magdalena.przezdziak','','');
