INSERT INTO `drupal_comments` VALUES (4482,0,1195,217,'Re: Wiceprezes ds. Rekrutacji, kandydatka: Aleksandra Szymańska','Tak jak pisałam w aplikacji, w tym roku miałam szansę dwa razy buddyniować:) \r\nZa każdym razem ogromną radość sprawiała mi możliwość zarażania innych swoją pasją do Stowarzyszenia. \r\nOd września pomagałam Piotrkowi przy rekrutacji rozwieszając plakaty informujące o rekrutacji, później rozdając setki ulotek na AE. Ta przygoda z CAŁYM procesem rekrutacyjnym była dla mnie wspaniałym doświadczeniem. Dała mi sporo radości oraz motywacji do dalszych działań i rozwoju w tym właśnie kierunku.','85.221.189.47',1240069718,0,4,'05/','aleksandra.szymanska','','');
