INSERT INTO `drupal_comments` VALUES (4546,0,973,140,'Re: Wieści od zaprzyjaźnionych z nami organizacji','[b]Zaproszenie na wyjazd w góry[/b]\r\n\r\nChcących odetchnąć od trudów nauki przed sesją, ośrodek Sołek zaprasza na czterodniowy wyjazd do Zakopanego! \r\n\r\nW planie między innymi:\r\n- zwiedzanie Zakopanego i okolic\r\n- dwudniowa wycieczka w góry połączona z noclegiem w schronisku na szlaku\r\n- góralskie ognisko\r\n\r\nWycieczkę prowadzi rodowity góral z Zakopanego :-)\r\n\r\n[u]Termin:[/u] 11-14.06.2009\r\n[u]Koszt:[/u] 220 zł\r\n\r\nMożliwości zgłoszenia: Józek Mrowca, koordynator wycieczki, mail: jozefmrowca@gmail.com, tel: (061) 841 75 74. \r\nInformacje bieżące można śledzić na stronie http://www.solek.pl\r\n\r\n\r\n','213.156.115.52',1242743958,0,4,'05/','alicja.staniuk','','');
