INSERT INTO `drupal_comments` VALUES (4557,0,1457,157,'Re: akcja.kula','Hej!\r\n\r\nBoisko jest w fazie rezerwacji. \r\nOznacza to, że jak tylko wybrany przez nas termin będzie wolny to gramy tam, gdzie ostatnio.\r\nJeżeli nie uda się zarezerwować wybranej godziny to będę lobbował na godzinę możliwie zbieżną z 16.\r\nJeżeli nie uda się w ogóle zarezerwować boiska przy akademikach to proponuję udać się na któreś z boisk niedaleko.\r\n\r\nDam znać\r\nPozdro\r\nZ','62.21.86.253',1243411490,0,4,'03/','zbigniew.jedrzejczyk','','');
