INSERT INTO `drupal_comments` VALUES (4601,0,1560,151,'Re: Wiceprezes ds. Rozwoju, kandydat - Marcin Kaczmarek','Z jednej strony podzielam i popieram Twoje zainteresowanie problemem umiejętności udzielania feedbacku, z drugiej strony zastanawia mnie zawarta w Twojej aplikacji deklaracja \"przewartościowania treści podręcznika trenera\". Czy mógłbyś rozjaśnić ten temat i powiedzieć troszeczkę więcej nt. tegoż przewartościowania?\r\n\r\nI pytanie drugie: Co jest dla Ciebie najważniejsze w Stowarzyszeniu? ','79.184.193.133',1256154741,0,4,'01/','piotr.kwiotek','','');
