INSERT INTO `drupal_comments` VALUES (4671,0,1608,206,'Re: Zmiany, poprawki, ustawki - techniczne','Olu, więkość tych rzeczy o których napisałaś jest mi również wiadoma.\r\n[list]\r\n[*] logowanie udało mi się przerobić, żeby tak działało,\r\n[*] nie bardzo rozumiem o co chodzi ze stroną główną,\r\n[*] podział postów pewnie można by lepiej zaakcentować, ale na razie będzie to miało bardzo niski priorytet,\r\n[*] problem motta załatwiłem tak, że już wcześniej wyłączyłem je globalnie dla całego forum,\r\n[*] przybornik to duży temat, bo trzeba by było dokonać solidnego przeglądu modułów, również pod kątem użycia ich do edycji publicznych stron.\r\n[/list]','94.245.127.12',1265292516,0,4,'09/','marcin.mikolajczak','','');
