INSERT INTO `drupal_comments` VALUES (4706,0,1580,217,'Re: Zespół ds. Rekrutacji 2009/2010','Chciałabym Wam zaprezentować składy 3 zespołów buddyniowych i ich zadania:) \r\n\r\n1 zespół:\r\nBuddy: Ada Siłakiewicz\r\nTeam:Karolina Marciniec, Radosław Małek, Piotr Krużyński\r\nZadanie:Przygotowanie propozycji nowego niezbędnika\r\n\r\n2 zespół:\r\nBuddy: Milena Bartnik\r\nTeam: Stanisław Cieślak, Jeremiasz Koper, Anna Grzywińska\r\nZadanie: Stworzenie i uaktualnieniu pakietu manuali dla UEP, PP i UAM \r\n\r\n3 zespół:\r\nBuddy: Marcin Waligóra\r\nTeam: Karolina Lubiejewska, Marzena Studniarska, Szymon Okoń, Michał Balcerzak\r\nZadanie: Przygotowanie szablonu podziękowań dla partnerów Stowarzyszenia\r\n\r\nPrezentacja przed całym Stowarzyszeniem efektów prac zespołów planowana jest na 15 kwietnia podczas Spotkania Ogólnego:)\r\n\r\n\r\n\r\n\r\n\r\n\r\n','213.156.120.199',1267390861,0,4,'03/','aleksandra.szymanska','','');
