INSERT INTO `drupal_comments` VALUES (4772,0,1783,181,'Re: Absolutorium - Koordynator ds. Marketingu - Anna Śliwińska','Aniu, dziękuję za to, że zawsze byłaś w Marketingu, mega-dobrze i sumiennie pracowałaś z całym Zespołem, nierzadko z osobistym poświęceniem. Dzięki temu nawet jak musiałaś zwolnić prowadzenie Zespołu, mogliśmy być cały czas spokojni o to, że Marketing jest na dobrym torze i będzie dalej jechał do przodu :)','85.221.211.156',1275049365,0,4,'01/','mateusz.matloka','','');
