INSERT INTO `drupal_comments` VALUES (4774,0,1779,181,'Re: Absolutorium - Wiceprezes ds. Kontaktów Zewnętrznych - ...','Kamilu, dziękuję za to, że bez słowa narzekań wykonałeś ogromną porcję niełatwej i czasem niewdzięcznej pracy. Za zainteresowanie Stowarzyszeniem na każdym kroku i dzielne przypominanie o rzeczach trudnych i ważnych.','85.221.211.156',1275049770,0,4,'02/','mateusz.matloka','','');
INSERT INTO `drupal_comments` VALUES (4775,0,1776,181,'Re: Absolutorium - Wiceprezes ds. Rozwoju - Marcin Kaczmarek','Marcinie, dziękuję za odwagę w podjęciu niełatwego wyzwania, za świeże spojrzenie na Stowarzyszenie i umiejętność dostrzegania niewidocznych na pierwszy rzut oka, a istotnych problemów.','85.221.211.156',1275049915,0,4,'02/','mateusz.matloka','','');
