INSERT INTO `drupal_locales_target` VALUES (15187,'Choć bloki są zazwyczaj dostarczane przez moduły (jak to jest w wypadku bloku logowania), administratorzy również mogą je tworzyć. Bloki posiadają tytuł, opis i zawartość. Zawartość ta może być dowolnie długa i może korzystać z jednego z dostępnych <a href=\"@input-format\">formatów danych</a>.','pl',0,0,0);
