INSERT INTO `drupal_locales_target` VALUES (15227,'Opcjonalny element menu nawigacyjnego, <em>Blogi</em>, służy do wyświetlania wszystkich blogów dostępnych w witrynie, a element <em>Mój blog</em> przenosi użytkownika na stronę wyświetlającą jego ostatnie wpisy. <em>Wpis w blogu</em> z menu <em>Dodaj zawartość</em> umożliwia tworzenie nowych wpisów.','pl',0,0,0);
