INSERT INTO `drupal_locales_target` VALUES (15353,'Moduł książek nadaje się szczególnie do tworzenia uporządkowanych, wielostronicowych dokumentów hipertekstowych, takich jak podręczniki, przewodniki czy zbiory odpowiedzi na najczęściej zadawane pytania (ang. Frequently Asked Questions, FAQ). Książki pozwalają bowiem na tworzenie rozdziałów, działów, etc. Autorzy wyposażeni w odpowiednie uprawnienia mogą umieszczać strony w książce.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15354,'Książki wyposażone są w dodatkowe odnośniki nawigacyjne, ułatwiające poruszanie się po tekście. Odnośniki te prowadzą do poprzedniej i kolejnej strony w książce, a także do strony nadrzędnej względem aktualnie przeglądanej. Dodatkowe możliwości nawigacji po książkach oferuje blok <em>Książka</em>, który można włączyć na <a href=\"@admin-block\">stronie zarządzania blokami</a>.','pl',0,0,0);
