INSERT INTO `drupal_locales_target` VALUES (15415,'Uprawnienia do dodawania odpowiedzi przydziela się rangom użytkowników, na przykład użytkownikom anonimowym. Jeśli anonimowi użytkownicy mają możliwość dodawawania odpowiedzi, dane kontaktowe, które pozostawiają, mogą być przechowywane w plikach \"cookie\" na ich własnych komputerach, co eliminuje konieczność ponownego wprowadzania tych samych danych podczas dodawania kolejnych odpowiedzi. Jeżeli do danej odpowiedzi nie ustosunkował się żaden użytkownik, może ona zostać zmieniona przez jej autora. Moduł wykorzystuje te same formaty danych, które są dostępne podczas tworzenia wpisów.','pl',0,0,0);
