INSERT INTO `drupal_locales_target` VALUES (15599,'Moduł obsługi dzienników monitoruje działanie systemu, zachowując informacje o zdarzeniach w dzienniku, który może być później przeglądany przez użytkownika posiadającego odpowiednie uprawnienia. Dziennik jest przydatny administratorom, którzy chcą mieć dostęp do zwięzłego przeglądu zdarzeń występujących w witrynie. Dzienniki zachowują także takie informacje, które mogą być przydatne do rozwiązywania problemów w funkcjonowaniu witryny.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15600,'Dziennik jest po prostu listą zapisanych zdarzeń, zawierająca informacje na temat użytkowników, wydajności, błędów, ostrzeżeń i informacje o działaniu systemu. Bardzo ważne jest, by regularnie przeglądać dzienniki, które są często jedynym źródłem informacji na temat tego, co dzieje się w witrynie.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15601,'Więcej informacji na <a href=\"@dblog\">stronę dotyczącej dzienników zdarzeń</a> w podręczniku konfiguracji w internecie.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15602,'Moduł obsługi dzienników monitoruje działanie witryny, zachowując informacje o zdarzeniach w dzienniku, który może być później przeglądany przez użytkownika posiadającego odpowiednie uprawnienia. Dziennik jest po prostu listą zapisanych zdarzeń, zawierająca informacje na temat użytkowników, wydajności, błędów, ostrzeżeń i informacje o działaniu systemu. Bardzo ważne jest, by regularnie przeglądać dzienniki, które są często jedynym źródłem informacji na temat tego, co dzieje się w witrynie.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15603,'Ustawienia dotyczące zapisywania zdarzeń w dziennikach przechowywanych w bazie danych. Metoda ta jest najczęściej wykorzystywana w przypadku małych i średnich witryn nie posiadających własnego serwera. Dzienniki te można przeglądać za pomocą stron dostępnych w dziale zarządzania.','pl',0,0,0);
