INSERT INTO `drupal_locales_target` VALUES (15628,'Jeśli w witrynie pozostała jakakolwiek zawartość wprowadzona w tym formacie, zostanie ona przekonwertowana do formatu domyślnego. Operacja ta jest nieodwracalna.','pl',0,0,0);
