INSERT INTO `drupal_locales_target` VALUES (15650,'zarządzanie filtrami','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15651,'\n<p>Ta witryna pozwala na publikowanie zawartości w formacie HTML. Choć perspektywa nauki wszystkich znaczników HTML może onieśmielać, nauczenie się tylko kilku podstawowych jest bardzo proste. Tabela przedstawia przykładowe zastosowania dostępnych na tej stronie znaczników.</p>\n<p>Więcej informacji uzyskać można na <a href=\"http://www.w3.org/TR/html/\">stronie specyfikacji HTML</a> na serwerze W3C lub poprzez wyszukiwarkę internetową.</p>','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15652,'Znaczniki zwane kotwicami służą do tworzenia odnośników do innych stron.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15653,'Domyślnie znaczniki nowych linii wstawiane są automatycznie, więc z takich znaczników należy korzystać tylko do wprowadzenia dodatkowych odstępów. Znacznik nowej linii różni się od innych, ponieważ nie występuje w dwóch wariantach (otwierającym i zamykającym). By być w zgodzie ze standardem XHTML 1.0, należy postawić znak \" /\" wewnątrz znacznika.','pl',0,0,0);
