INSERT INTO `drupal_locales_target` VALUES (15770,'Moduł pozwala tworzyć fora dyskusyjne. Dają one członkom społeczności skupionej wokół witryny możliwość prowadzenia rozmów, do zapisów których można powrócić w dowolnym czasie. Odnośnik <a href=\"@create-topic\">Temat na forum</a>, znajdujący się w menu <em>Dodaj zawartość</em>, służy do rozpoczęcia dyskusji.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15771,'Gdy ludzie odpowiadają osobie, która zapoczątkowała temat, lub sobie nawzajem, tworzą się wątki. Tematy są umieszczane w forach, które mogą zawierać wiele podobnych lub powiązanych ze sobą tematów. Fora z kolei można umieszczać w działach. Zarówno fora, jak i działy - w innych forach lub działach. Pozwala to rozplanować strukturę forum. Jeśli zrobi się to uważnie, użytkownikom będzie łatwiej znaleźć interesujące ich tematy i rozpoczynać nowe.','pl',0,0,0);
