INSERT INTO `drupal_locales_target` VALUES (15778,'Na stronie znajduje się lista istniejących forów i działów. Działy mogą przechowywać fora, a fora - tematy. Zarówno fora, jak i działy, można umieszczać wewnątrz innych forów lub działów. Ich układ można zmienić, przeciągając je w wybrane miejsca za pomocą uchwytów znajdujących się w kolumnie <em>Nazwa</em>. Zmiany zostaną wprowadzone dopiero po skorzystaniu z przycisku <em>Zachowaj</em> u dołu strony.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15779,'Działy pomagają uporządkować fora. Dział o nazwie \"Jedzenie\" mógłby na przykład zawierać fora \"Owoce\" i \"Warzywa\".','pl',0,0,0);
