INSERT INTO `drupal_locales_target` VALUES (15905,'Określa, czy można tworzyć wersje językowe wpisów tego rodzaju. Jeśli tak, w formularzu edycji wpisu wyświetlane jest pole umożliwiające wskazanie jednego z <a href=\"!languages\">dostępnych języków</a>. W przeciwnym wypadku nowe treści przypisywane są do języka domyślnego. Zmiana tego ustawienia nie wpłynie na dotychczas dodane wpisy.','pl',0,0,0);
