INSERT INTO `drupal_locales_target` VALUES (15916,'Ścieżka, do której ma prowadzić menu. Może to być wewnętrzna ścieżka Drupala, taka jak %add-node, lub adres zewnętrznej strony, jak na przykład %drupal. Wpisanie %front sprawi, że odnośnik będzie prowadził do strony głównej.','pl',0,0,0);
