INSERT INTO `drupal_locales_target` VALUES (15923,'Opcjonalna. Lżejsze elementy znajdą się wyżej, a cięższe niżej.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (15924,'System menu umożliwia wprowadzanie jedynie oryginalnych ścieżek, choć korzysta z aliasów podczas wyświetlania odnośników. Adres %link_path został zapisany jako %normal_path','pl',0,0,0);
