INSERT INTO `drupal_locales_target` VALUES (16215,'Próba rejestracji przy użyciu OpenID nie powiodła się z powodów wymienionych poniżej. Można zarejestrować się w zwykły sposób, lub - w przypadku posiadania konta w witrynie - <a href=\"@login\">zalogować się</a> i dodać identyfikator OpenID poprzez stronę \"Moje konto\".','pl',0,0,0);
