INSERT INTO `drupal_locales_target` VALUES (16232,'Alternatywna ścieżka, poprzez którą będzie można uzyskać dostęp do danych. Jeśli właśnie tworzona strona będzie traktować o poezji, można wpisać \"wiersze\". Ścieżka powinna być względna i nie może kończyć się ukośnikiem.','pl',0,0,0);
