INSERT INTO `drupal_locales_target` VALUES (16255,'Tworzony właśnie segment może być dostępny pod alternatywnym adresem URL. Jeśli strona ma dotyczyć ciekawostek, można ją na przykład umieścić pod adresem \"ciekawostki\". Ścieżki powinny być względne i nie mogą kończyć się ukośnikiem.','pl',0,0,0);
