INSERT INTO `drupal_locales_target` VALUES (16276,'Warto rozważyć umieszczenie kodu w nowym module witryny lub wewnątrz pliku <code>template.php</code>.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16277,'Uruchamianie kodu PHP jest możliwe tylko, jeśli włączony jest moduł filtra PHP. Gdy moduł ten zostanie wyłączony lub usunięty, bloki i wpisy, w których zawarto kod, będą go wyświetlać zamiast uruchamiać.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16278,'Przykładowe zastosowanie: <em>stworzenie bloku z powitaniem dla odwiedzających</em>.','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16279,'<p>Dodaj blok zatytułowany \"Witamy\". Format danych powinien być ustawiony na \"Kod PHP\". W treści bloku należy wpisać:</p>\n<pre>\nprint t(\'Witamy! Dziękujemy, że tracisz swój czas na czytanie tego bezsensownego powitania!\');\n</pre>','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16280,'<p>Kod wyświetlający nazwę konta uwierzytelnionego użytkownika wygląda tak:</p>\n<pre>\nglobal $user;\nif ($user->uid) {\n  print t(\'Witamy, @name! Dziękujemy, że tracisz swój czas na czytanie tego bezsensownego powitania!\', array(\'@name\' => $user->name));\n}\nelse {\n  print t(\'Witamy! Dziękujemy, że tracisz swój czas na czytanie tego bezsensownego powitania!\');\n}\n</pre>','pl',0,0,0);
