INSERT INTO `drupal_locales_target` VALUES (16410,' Indeks wyszukiwania nie jest czyszczony, ale systematycznie aktualizowany, by odpowiadał nowym ustawieniom. Wyszukiwanie nadal będzie możliwe, jednak nowa zawartość nie zostanie zindeksowana, dopóki system nie zindeksuje dotychczasowej. Operacja jest nieodwracalna.','pl',0,0,0);
