INSERT INTO `drupal_locales_target` VALUES (16418,'Maksymalna liczba elementów, które mogą zostać zindeksowane podczas wykonywania zadań cron. Jeżeli to konieczne, można zmniejszyć tę liczbę, by zapobiec opóźnieniom i zbyt wielkiemu wykorzystaniu pamięci.','pl',0,0,0);
