INSERT INTO `drupal_locales_target` VALUES (16420,'<p><em>Zmiana poniższego ustawienia spowoduje przebudowanie indeksu witryny. Indeks wyszukiwania nie jest czyszczony, ale systematycznie aktualizowany, by odpowiadał nowym ustawieniom. Wyszukiwanie nadal będzie działać, jednak nowa zawartość nie zostanie uwzględniona, póki nie stanie się to z całą dotychczasową.</em></p><p><em>Domyślne ustawienia powinny być odpowiednie dla większości witryn.</em></p>','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16421,'Minimalna długość słowa (indeksowanie)','pl',0,0,0);
INSERT INTO `drupal_locales_target` VALUES (16422,'Minimalna liczba znaków, z których musi się składać słowo, by mogło zostać zindeksowane. Mniejsza liczba oznacza dokładniejsze wyniki wyszukiwania, ale i większe wykorzystanie bazy danych. Każde zapytanie do wyszukiwarki musi zawierać przynajmniej jedno słowo kluczowe takiej długości (lub dłuższe).','pl',0,0,0);
