INSERT INTO `drupal_locales_target` VALUES (16424,'Podstawowa obsługa języka chińskiego, japońskiego i koreańskiego, bazująca na powtarzających się sekwencjach symboli. Jeśli do tego celu wykorzystywane będzie zewnętrzne narzędzie, obsługę należy wyłączyć. Ustawienie to nie ma wpływu na inne języki.','pl',0,0,0);
